import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GuestsForm = () => {
  const [guests, setGuests] = useState([]);

  useEffect(() => {
    // Funci칩n para cargar la lista de invitados al montar el componente
    const fetchGuests = async () => {
      try {
        const response = await axios.get('http://localhost:8080/guests');
        setGuests(response.data);
      } catch (error) {
        console.log('Error al obtener la lista de invitados:', error);
      }
    };

    fetchGuests();
  }, []); // El segundo par치metro es un array de dependencias para ejecutar el efecto solo una vez al montar el componente

  return (
    <div>
      <h2>Lista de Invitados</h2>
       <button onClick={handleRefresh}>Actualizar Lista</button>
      <ul>
        {guests.map((guest) => (
          <li key={guest.mail}>
            {guest.name} {guest.surname} - {guest.mail}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GuestsForm;
